package com.design.factory.factory;import com.design.factory.Vehicle;import com.design.factory.VehicleFactory;/** * @author Zhang Yu */public class CarFactory extends VehicleFactory {    @Override    protected Vehicle createVehicle(String item) {        switch (item) {            case "large":                return new LargeCar();            case "small":                return new SmallCar();            default:                return null;        }    }    public static void main(String[] args) {        VehicleFactory vehicleFactory = new CarFactory();        Vehicle largeCar = vehicleFactory.create("large");        largeCar.product();    }}