package com.design.singleton;/** * @author Zhang Yu * 类一旦加载就创建一个单例，保证在调用 getInstance 方法之前单例已经存在了 */public class HungrySingleton {    private static final HungrySingleton instance = new HungrySingleton();    private HungrySingleton() {        System.out.println("饿汉模式");    }    public static HungrySingleton getInstance() {        return instance;    }    public void doSomething() {        System.out.println("doing");    }    public static void main(String[] args) {        HungrySingleton instance = HungrySingleton.getInstance();        instance.doSomething();        HungrySingleton instance1 = HungrySingleton.getInstance();        instance1.doSomething();        System.out.println(instance == instance1);    }}