package com.design.singleton;/** * @author Zhang Yu * 加载时没有生成单例，只有当第一次调用 getlnstance 方法时才去创建这个单例 */public class LazySingleton {    private static volatile LazySingleton instance = null;    private LazySingleton() {        System.out.println("懒汉模式");    }    public static synchronized LazySingleton getInstance() {        if (instance == null) {            instance = new LazySingleton();        }        return instance;    }    public void doSomething() {        System.out.println("doing");    }    public static void main(String[] args) {        LazySingleton instance = LazySingleton.getInstance();        instance.doSomething();        LazySingleton instance1 = LazySingleton.getInstance();        instance1.doSomething();        System.out.println(instance == instance1);    }}