package com.design.singleton;/** * @author Zhang Yu * 单例模式：一个对象只能创建一个实例，除此以为，它还提供了对实例的全局访问方法 */public class Singleton {    private static Singleton instance;    private Singleton() {        System.out.println("单例模式");    }    public static Singleton getInstance() {        if (instance == null) {            instance = new Singleton();        }        return instance;    }    public void doSomething() {        System.out.println("doing");    }    public static void main(String[] args) {        Singleton instance = Singleton.getInstance();        instance.doSomething();    }}